            mapper.addUser(user);
            //5.提交事务
            sqlSession.commit();
        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            if (sqlSession != null) {
            //6.最后一定不要忘记关闭sqlSession。
                sqlSession.close();
            }
        }
    }
}

