    public Map register(UserAuth userAuth) {
        Map result = new HashMap<>();
        //验证非空
        if (userAuth == null || isEmpty(userAuth)){
            result.put("code", FinalValue.ERROR);
            result.put("msg", "用户名或密码为空");
            return result;
        }
        String telPhone = userAuth.getTelPhone();
        // 验证是否重复
        int count = userMapper.findUserByTelPhone(telPhone);
        if (count > 0){
            result.put("code", FinalValue.ERROR);
            result.put("msg", "手机号已存在");
            return result;
        }
        // 验证手机号的格式是否正确
        String patten = "1[34578]\\d{9}";

        boolean isTelPhone = Pattern.matches(patten, telPhone);
        //如果不是正确的手机号
        if (!isTelPhone){
            result.put("code", FinalValue.ERROR);
            result.put("msg", "手机号格式不正确");
            return result;
        }
        //TODO 验证密码是否符合格式

        // MD5 加密
        userAuth.setPassword(MD5.MD5EncodeUtf8(userAuth.getPassword()));
        // 设置 权限
        userAuth.setRole(FinalValue.CUSTOM);
        // 保存
        int line  = userMapper.insertUser(userAuth);
        // 获取保存的结果
        if (line == 0){
            result.put("code", FinalValue.ERROR);
            result.put("msg", "注册失败，穷重试");
            return result;
        }
        result.put("code", FinalValue.SUCCESS);
        result.put("msg", "注册成功");
        return result;
    }
}

